-- Table structure for table `siswa`
--

CREATE TABLE `siswa` (
  `id` int(11) NOT NULL,
  `no_formulir` varchar(255) DEFAULT NULL,
  `guru_id` int(11) DEFAULT NULL,
  `orang_id` int(11) DEFAULT NULL,
  `status` enum('b','a','p','n') DEFAULT 'b',
  `tanggal_pendaftaran` date DEFAULT (now()),
  `komisi` int(11) DEFAULT NULL,
  `pinyin` varchar(255) DEFAULT NULL,
  `dengar` varchar(255) DEFAULT NULL,
  `bicara` varchar(255) DEFAULT NULL,
  `membaca` varchar(255) DEFAULT NULL,
  `menulis` varchar(255) DEFAULT NULL,
  `kondisi` varchar(255) DEFAULT NULL,
  `respon` varchar(255) DEFAULT NULL,
  `tanggapan` varchar(255) DEFAULT NULL,
  `program` varchar(255) DEFAULT NULL,
  `paket_belajar` varchar(255) DEFAULT NULL,
  `referal_other` varchar(255) DEFAULT NULL,
  `kursus_id` int(11) DEFAULT NULL,
  `sekolah` varchar(255) DEFAULT NULL,
  `kelas` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

